+<!DOCTYPE html>
+<html lang="en">
+<head>
+  <meta charset="UTF-8" />
+  <meta http-equiv="X-UA-Compatible" content="IE=edge" />
+  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
+  <title><%= typeof pagetitle !== 'undefined' ? pagetitle : 'Awesome Shop' %></title>
+  
+  <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
+
+  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet" />
+  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css" />
+  <link rel="stylesheet" href="/stylesheets/style.css" />
+
+  <style>
+    body {
+      display: flex;
+      flex-direction: column;
+      min-height: 100vh;
+      margin: 0;
+    }
+    .main-content {
+      flex: 1 0 auto; /* Ensures main takes up available space */
+    }
+    
+    /* FIX: Dropdown and Navbar Layering */
+    .custom-navbar {
+      z-index: 1030; /* Bootstrap standard for sticky-top */
+    }
+    .dropdown-menu {
+      z-index: 1050 !important; /* Ensure dropdowns stay on top */
+    }
+
+    .flash-container {
+      position: fixed;
+      top: 80px;
+      right: 20px;
+      z-index: 2000;
+      max-width: 400px;
+    }
+  </style>
+</head>
+
+<body>
+  <%- include("./layouts/partials/header") %>
+
+  <% if(typeof(flash) != "undefined" && flash.message) { %>
+    <div class="flash-container">
+      <div class="alert alert-<%= flash.type %> alert-dismissible fade show shadow-sm border-0" role="alert">
+        <i class="fa-solid fa-circle-info me-2"></i>
+        <%= flash.message %>
+        <button type="button" class="btn-close shadow-none" data-bs-dismiss="alert" aria-label="Close"></button>
+      </div>
+    </div>
+  <% } %>
+
+  <main class="main-content">
+    <div class="container py-4">
+      <%- body %>
+    </div>
+  </main>
+
+  <%- include("./layouts/partials/footer") %>
+
+  <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.bundle.min.js"></script>
+
+  <script>
+    document.addEventListener("DOMContentLoaded", () => {
+      document.querySelectorAll('[data-bs-toggle="dropdown"]').forEach((el) => {
+        const dd = bootstrap.Dropdown.getOrCreateInstance(el);
+
+        el.addEventListener("click", (e) => {
+          e.preventDefault();
+          e.stopPropagation();
+          dd.toggle();
+        });
+      });
+
+      document.addEventListener("click", () => {
+        document.querySelectorAll('[data-bs-toggle="dropdown"]').forEach((el) => {
+          const dd = bootstrap.Dropdown.getInstance(el);
+          if (dd) dd.hide();
+        });
+      });
+    });
+  </script>
+</body>
+</html>
